import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import Book from './Book';
import * as BooksAPI from './BooksAPI';
/*
	Created by Akshay Niranjan
	Last Modified
	12th May 2021
*/
class Search extends Component {
    static propTypes = {
        book_array: PropTypes.array.isRequired,
        changeShelfFunc: PropTypes.func.isRequired
    };

    state = {
        query: '',
        newBooks: [],
        searchErr: false
    };

    getBooks = event => {//using book api we can search to find a list of books
        const query = event.target.value;
        this.setState({ query });
        let words = query.split(" ");
        let book_filter = [[]];
        //run search if user input is found
        for (const word in words) {
            BooksAPI.search(word, 100).then(book_array => {
                book_array.length > 0 ? this.setState({ newBooks: book_array, searchError: false })
                    : this.setState({ newBooks: [], searchError: true });
            });
        }
        if (book_array.length > 0) {
            this.setState({ newBooks: book_array, searchError: false })
        }
        else
            this.setState({ newBooks: [], searchError: true });
    };

    render() {
        const { query, newBooks, searchError } = this.state;
        const { book_array, changeShelfFunc } = this.props;
        return (
            <div className="search-books">
                <div className="search-books-bar">
                    <Link className="close-search" to="/">Close</Link>
                    <div className="search-books-input-wrapper">
                        <input
                            type="text"
                            placeholder="Search by title or author"
                            value={query}
                            onChange={this.getBooks}
                        />
                    </div>
                </div>
                <div className="search-books-results">
                    {newBooks.length > 0 && (
                        <div>
                            <h3>Search returned {newBooks.length} books </h3>
                            <ol className="books-grid">
                                {newBooks.map(book => (
                                    <Book
                                        book={book}
                                        book_array={book_array}
                                        key={book.id}
                                        changeShelfFunc={changeShelfFunc}
                                    />
                                ))}
                            </ol>
                        </div>
                    )}
                    {searchError && (<h3>Search did not return any books. Please try again!</h3>)}
                </div>
            </div>
        );
    }//accidentally didnt tab render, fix later
}
export default Search;
